UNION ALL SELECT id, 'Runbooks & Operational Guides', 'Create operational documentation. Include: 1) Deployment procedures, 2) Monitoring setup, 3) Troubleshooting steps, 4) Incident response, 5) Rollback procedures, 6) Configuration management, 7) Disaster recovery, 8) Contact information. Provide runbook templates using Australian spelling.', 8 FROM prompt_packs WHERE name = 'Documentation Writing'
UNION ALL SELECT id, 'Onboarding Documentation', 'Design onboarding docs. Include: 1) Environment setup, 2) Codebase overview, 3) Development workflow, 4) Testing procedures, 5) Deployment process, 6) Team contacts, 7) Learning resources, 8) First tasks guide. Provide onboarding checklist using Australian spelling.', 9 FROM prompt_packs WHERE name = 'Documentation Writing'
UNION ALL SELECT id, 'Documentation Tools & Automation', 'Implement documentation automation. Include: 1) Documentation generators, 2) API doc automation, 3) Diagram generation, 4) Version management, 5) Search implementation, 6) Hosting solutions, 7) Update workflows, 8) Analytics tracking. Provide tooling setup using Australian spelling.', 10 FROM prompt_packs WHERE name = 'Documentation Writing';